import { useDispatch } from "react-redux";
import Card from "../UI/Card";

import shoeSvg from "../../images/subject.svg";

import { cartLogicActions } from "../store/cart-slice";
import styled from "styled-components";
const ProductItem = (props) => {
  const { title, price, description, id, text } = props;
  const dispatch = useDispatch();
  const addItemToCartHandler = () => {
    // fetch('',{method:'POST',body:JSON.stringify(newCart)})
    dispatch(
      cartLogicActions.addItemToCart({
        id,
        title,
        price,
      })
    );
  };

  return (
    <Wrapper>
      <OneSection>
        <H1>{title}</H1>
        <H3>{text}</H3>
        <div>${price.toFixed(2)}</div>

        <p>{description}</p>

        <button onClick={addItemToCartHandler}>Add to Cart</button>
      </OneSection>
      <SecondSection>
        <img src={shoeSvg} />
      </SecondSection>
    </Wrapper>
  );
};

export default ProductItem;
const Wrapper = styled.div`
  display: grid;
  grid-template-columns: 400px auto;
  align-items: center;
  width: 50%;
  margin: 0px auto;
`;
const OneSection = styled.div`
  width: 500px;
`;

const H1 = styled.div`
  max-width: 600px;

  font-family: "Black Ops One";
  font-style: normal;
  font-weight: 400;
  font-size: 58px;
  padding: 0px;
  line-height: 197.02%;
  /* or 252px */

  letter-spacing: 0.05em;

  color: #ffffff;
`
const H3 =styled.div`


width: 411px;
height: 40px;


font-family: 'Cabin';
font-style: normal;
font-weight: 400;
font-size: 30px;
line-height: 197.02%;
/* or 79px */

letter-spacing: 0.05em;

color: #FFFFFF;

`
const SecondSection = styled.div`
  padding: 0px 80px;
`;
